import { useState } from "react";
import DataGrid from "./DataGrid";
import "./Input.css";

const Input = () => {
  const [fileResult, setFileResult] = useState();
  const [isFileUploaded, setIsFileUploaded] = useState(false);

  const oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds

  const sfHandler = (e) => {
    setIsFileUploaded(true);
    console.log(fileResult);
  };

  // Callback from a <input type="file" onchange="onChange(event)">
  function onChange(е) {
    var file = е.target.files[0];
    var reader = new FileReader();

    reader.onload = function (e) {
      // The file's text will be printed here
      console.log(e.target.result);
      const transformed = e.target.result.split("\r\n");
      const whatever = transformed.map((employee) => {
        return employee.split(",");
      });


      const doit = whatever.map(single => {
        return {
          employeeID: single[0],
          
        }
      })
      /*const doit = whatever.map((single) => {
        return {
          employeeID: single[0].trim(),
          projectID: single[1].trim(),
          daysWorked: Math.round(
            Math.abs(
              (new Date(single[2].trim().replaceAll("-", ",")) -
                new Date(single[3].trim().replaceAll("-", ","))) /
                oneDay
            )
          ),
        };
      });
*/
      setFileResult(doit);
    };
  
    reader.readAsText(file);
  }

  return (
    <div className="input-container">
      <label htmlFor="file-upload" className="custom-file-upload">
        Upload your file here
      </label>
      <input type="file" id="fileInput" onChange={onChange} />
      <button onClick={sfHandler}>dasdsadsa</button>
      {isFileUploaded && <DataGrid employeeStats={fileResult} />}
    </div>
  );
};

export default Input;
