import { useState } from "react";
import DataGrid from "./DataGrid";
import "./Input.css";

const Input = () => {
  const [fileResult, setFileResult] = useState();
  const [isFileUploaded, setIsFileUploaded] = useState(false);

  const oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds

  const sfHandler = (e) => {
    setIsFileUploaded(true);
    console.log(fileResult);
  };

  // Callback from a <input type="file" onchange="onChange(event)">
  function onChange(event) {
    var file = event.target.files[0];
    var reader = new FileReader();

    reader.onload = function (e) {
      // The file's text will be printed here
      console.log(e.target.result);
      const transformed = e.target.result.trim().split("\r\n");
      const whatever = transformed.map((employee) => {
        return employee.replaceAll("-", ",").split(",");
      });


      const doit1 = whatever.map(single => {
        return single.map(Number)
      })
     const doit = doit1.map((single) => {
       
        return {
          employeeID: single[0],
          projectID: single[1],
          daysWorked: Math.round(
            Math.abs(
              (new Date(single[2], single[3], single[4]) -
                new Date(single[5], single[6], single[7])) /
                oneDay
            )
          ),
        };
      });

      var groupBy = function(xs, key) {
        return doit.reduce(function(rv, x) {
          (rv[x[key]] = rv[x[key]] || []).push(x);
          return rv;
        }, {});
      };
      
      console.log(groupBy(['one', 'two', 'three'], 'length'));
      
      // => {3: ["one", "two"], 5: ["three"]}

      /*doit.sort(function (a, b) {   
        return b.projectID - a.projectID;
    });*/
      
      
      setFileResult(doit);
    };

    reader.readAsText(file);
  }

  return (
    <div className="input-container">
      <label htmlFor="file-upload" className="custom-file-upload">
        Upload your file here
      </label>
      <input type="file" id="fileInput" onChange={onChange} />
      <button onClick={sfHandler}>dasdsadsa</button>
      {isFileUploaded && <DataGrid employeeStats={fileResult} />}
    </div>
  );
};

export default Input;
