import DataGrid from "./DataGrid";
import "./Input.css";




const Input = () => {
const rrv = useLoadFile()
function wtf(event) {
useLoadFile()
}

  return (
    <div className="input-container">
      <div className="label-container">
        <label htmlFor="file-upload" className="custom-file-upload" onClick={wtf}>
          Upload your file here
        </label>
      </div>
      <input type="file" id="fileInput" onChange={wtf} />
      
    </div>
  );
}


export default Input;
