import { useState } from "react";

const useLoadFile = (event) => {

  const oneDay = 24 * 60 * 60 * 1000; // hours*minutes*seconds*milliseconds
  var today = new Date();

  function wtf(event) {
  var file = event.target.files[0];
  var reader = new FileReader();
  reader.onload = function (e) {
    /* split employees by row  */
    const splitEmployees = e.target.result.trim().split("\r\n");
    const employeesData = splitEmployees.map((employee) => {
      /* in case of NULL - get current date */
      return employee
        .replaceAll(
          "NULL",
          today.getFullYear() +
            "-" +
            (today.getMonth() + 1) +
            "-" +
            today.getDate()
        )
        .split(",");
        
    });
  }

return employeesData
          
  }
  
}

export default useLoadFile;
